<template>
<div class="breadcrumb">
    <el-breadcrumb separator="/">
      <el-breadcrumb-item @click="linkTo(v.path, k)" v-for="(v,k) in item" :key="k" :class="{'active': k === active}">{{ t(v.page) }}</el-breadcrumb-item>
    </el-breadcrumb>
</div>
</template>
<script setup>
import {nuxtTo} from "@/utils/tools";
import { onMounted } from "vue";
const { t } = useI18n();
const props = defineProps({
    item: {
        type:Array,
        default: ()=>[]
    }
})
let active = ref(props.item.length - 1)
const linkTo = (path, idx)=>{
    active.value = idx
    path&&nuxtTo(path)
}
</script>
<style scoped lang='scss'>
.breadcrumb{
    position: relative;
    z-index: 99;
    :deep(.el-breadcrumb__inner) {
        color: #9C9C9C;
        cursor: pointer;
        &:hover {
            color:#fff;
        }
    }
    :deep(.el-breadcrumb__item) {
        &.active {
            .el-breadcrumb__inner {
                color: #fff!important;
            }

        }
    }
}
</style>
