import {defineStore} from "pinia";
import {ref} from "vue";
import {getQuery} from "@/utils/tools";
//记录用户每天首次进入网站时间
export const useInnerTime = defineStore("innerTime", () => {
	const timeStr = ref("")
	const setInnerTime = () => {
		const now = new Date()
		const today = new Date(now.getFullYear(), now.getMonth(), now.getDate())
		const storeTime = getInnerTime()
		if(storeTime){
			const storeDate = new Date(storeTime)
                        if(storeDate.toDateString() === today.toDateString()){
	                        // 如果存储的时间是今天，则不更新
				timeStr.value = storeDate.toDateString()
                        }else {
	                        // 如果存储的时间不是今天，则更新
	                        useFirstVisitData().resetFirstVisitData()
				timeStr.value = now.toDateString()
                        }
		}else{
			// 如果没有存储时间，则存储当前时间
			useFirstVisitData().resetFirstVisitData()
			timeStr.value = now.toDateString()
		}
	}
	const getInnerTime = () => {
		return timeStr.value
	}

	return {timeStr, setInnerTime, getInnerTime}
}, {
	persist: {
		storage: persistedState.cookies
	}
})
//记录推广信息
export const useUtmData = defineStore("utmData", () => {
	const data = ref({})
	const setUtmData = (val) => {
		let obj = getQuery(val)
		if(Object.entries(obj).length>0){
			data.value = obj
		}
	}
	const getUtmData = () => {
		return data.value
	}

	return {data, setUtmData, getUtmData}
}, {
	persist: {
		storage: persistedState.cookies
	}
})
//设置首次访问数据
export const useFirstVisitData = defineStore("firstVisitData", () => {
	const data = ref({})
	const setFirstVisitData = (val,bool=false) => {
		val = !val ? 'home' : val
		if(!data.value[`${val}_bol`]){
			data.value[`${val}_bol`] = bool
		}
	}
	const getFirstVisitData = (val) => {
		val = !val ? 'home' : val
		return data.value[`${val}_bol`]
	}
	const resetFirstVisitData = () => {
		data.value = {}
	}
	return {data, setFirstVisitData, getFirstVisitData,resetFirstVisitData}
}, {
	persist: {
		storage: persistedState.cookies
	}
})

import { createError } from "../composables/error.js";
const intervalError = "[nuxt] `setInterval` should not be used on the server. Consider wrapping it with an `onNuxtReady`, `onBeforeMount` or `onMounted` lifecycle hook, or ensure you only call it in the browser by checking `import.meta.client`.";
export const setInterval = import.meta.client ? window.setInterval : () => {
  if (import.meta.dev) {
    throw createError({
      statusCode: 500,
      message: intervalError
    });
  }
  console.error(intervalError);
};

import {useNuxtApp} from "nuxt/app";
