import {LANGUAGE_LIST} from "@/config/language";
import {extractSubstringAfterMatch,getQuery} from "@/utils/tools";
//推广数据键值映射表
const utmKeysMap = {
	"id":{
		label:"utm_id",
		str:"utm_id|utmId"
	},
	"campaign":{
		label:"utm_campaign",
		str:"utm_campaign|utmCampaign"
