import {defineStore} from "pinia";
import {ref} from "vue";

export const useCheckMobile = defineStore("isMobile", () => {
	const bol = ref(false)
	const setItem = (data) => {
		bol.value = data
	}
	const getItem = () => {
		return bol.value
	}

	return {bol, setItem, getItem}
}, {
	persist: {
		storage: persistedState.cookies
	}
})
